pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "./defifranc/interfaces/IBorrowerOperations.sol";
import "./defifranc/interfaces/ITroveManagerHelpers.sol";
import "./defifranc/interfaces/IHintHelpers.sol";
import "./defifranc/interfaces/IDCHFToken.sol";

// Uncomment this line to use console.log
// import "hardhat/console.sol";

error HarvestaStaking__NotEnoughEther();
error HarvestaStaking__TroveIsActive();
error HarvestaStaking__TroveIsNotActive();
error HarvestaStaking__SenderIsNotYieldManager();

contract HarvestaStaking {
    using SafeMath for uint256;

    /* ====== DEFIFRANC Contracts ====== */

    IBorrowerOperations public borrowerOperations;
    IHintHelpers public hintHelpers;
    ITroveManagerHelpers public troveManagerHelper;

    /* ====== State Variables ====== */

    address private yieldManagerAddress;

    mapping(address => uint256) private totalStakedAssets;

    /* ====== Modifiers ====== */

    modifier hasNoTrove(address _asset) {
        bool _active = troveManagerHelper.isTroveActive(_asset, address(this));
        if (_active) {
            revert HarvestaStaking__TroveIsActive();
        }
        _;
    }

    modifier isTroveActive(address _asset) {
        bool _active = troveManagerHelper.isTroveActive(_asset, address(this));
        if (!_active) {
            revert HarvestaStaking__TroveIsNotActive();
        }
        _;
    }

    modifier isYieldManager() {
        if (msg.sender != yieldManagerAddress) {
            revert HarvestaStaking__SenderIsNotYieldManager();
        }
        _;
    }

    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    // Fallback function is called when msg.data is not empty
    fallback() external payable {}

    constructor(
        address _borrowerOperations,
        address _hintHelpers,
        address _troveManagerHelpers
    ) {
        borrowerOperations = IBorrowerOperations(_borrowerOperations);

        hintHelpers = IHintHelpers(_hintHelpers);

        troveManagerHelper = ITroveManagerHelpers(_troveManagerHelpers);
    }

    function openTroveWithHint(
        address _asset,
        uint256 _collEther,
        uint256 _amountDebt
    ) external hasNoTrove(_asset) {}

    function adjustTroveWithHint() external isYieldManager {}

    function openTrove(
        address _asset,
        uint256 _collEther,
        uint256 _amountDebt
    ) external hasNoTrove(_asset) {
        uint256 _maxFee = 5e16;

        _openTrove(
            _asset,
            _maxFee,
            _amountDebt,
            _collEther,
            address(this),
            address(this)
        );
    }

    function _openTrove(
        address _asset,
        uint256 _maxFee,
        uint256 _amountDebt,
        uint256 _collEther,
        address _upperHint,
        address _lowerHint
    ) internal {
        if (_collEther > getBalance()) {
            revert HarvestaStaking__NotEnoughEther();
        }
        if (_asset == address(0)) {
            borrowerOperations.openTrove{value: _collEther}(
                _asset,
                _collEther,
                _maxFee,
                _amountDebt,
                _upperHint,
                _lowerHint
            );
        }
    }

    function adjustTrove(
        address _asset,
        uint256 _assetSent,
        uint256 _maxFee,
        uint256 _collWithdrawal,
        uint256 _debtChange,
        bool isDebtIncrease,
        address _upperHint,
        address _lowerHint
    ) internal {}

    function getBalance() internal view returns (uint256) {
        return address(this).balance;
    }
}
","keccak256":"0xe6959ff3289ef3dae5c8b0348feb00e31a914e1340226f132e815a28f2204e5e","license":"UNLICENSED"},"contracts/defifranc/interfaces/IActivePool.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.14;
import "./IPool.sol";

interface IActivePool is IPool {
    // --- Events ---
    event BorrowerOperationsAddressChanged(
        address _newBorrowerOperationsAddress
    );
    event TroveManagerAddressChanged(address _newTroveManagerAddress);
    event ActivePoolDCHFDebtUpdated(address _asset, uint256 _DCHFDebt);
    event ActivePoolAssetBalanceUpdated(address _asset, uint256 _balance);

    // --- Functions ---
    function sendAsset(
        address _asset,
        address _account,
        uint256 _amount
    ) external;
}
","keccak256":"0x03a25898270b074d663a6e15cf91caefaa8256f2d739e3f2bf5b7b33b7add2a8","license":"MIT"},"contracts/defifranc/interfaces/IBorrowerOperations.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.14;

// Common interface for the Trove Manager.
interface IBorrowerOperations {
    // --- Events ---

    event TroveManagerAddressChanged(address _newTroveManagerAddress);
    event StabilityPoolAddressChanged(address _stabilityPoolAddress);
    event GasPoolAddressChanged(address _gasPoolAddress);
    event CollSurplusPoolAddressChanged(address _collSurplusPoolAddress);
    event SortedTrovesAddressChanged(address _sortedTrovesAddress);
    event DCHFTokenAddressChanged(address _dchfTokenAddress);
    event MONStakingAddressChanged(address _MONStakingAddress);

    event TroveCreated(
        address indexed _asset,
        address indexed _borrower,
        uint256 arrayIndex
    );
    event TroveUpdated(
        address indexed _asset,
        address indexed _borrower,
        uint256 _debt,
        uint256 _coll,
        uint256 stake,
        uint8 operation
    );
    event DCHFBorrowingFeePaid(
        address indexed _asset,
        address indexed _borrower,
        uint256 _DCHFFee
    );

    // --- Functions ---

    function setAddresses(
        address _troveManagerAddress,
        address _troveManagerHelpersAddress,
        address _stabilityPoolAddress,
        address _gasPoolAddress,
        address _collSurplusPoolAddress,
        address _sortedTrovesAddress,
        address _dchfTokenAddress,
        address _MONStakingAddress,
        address _dfrancParamsAddress
    ) external;

    function openTrove(
        address _asset,
        uint256 _tokenAmount,
        uint256 _maxFee,
        uint256 _DCHFamount,
        address _upperHint,
        address _lowerHint
    ) external payable;

    function addColl(
        address _asset,
        uint256 _assetSent,
        address _upperHint,
        address _lowerHint
    ) external payable;

    function moveETHGainToTrove(
        address _asset,
        uint256 _amountMoved,
        address _user,
        address _upperHint,
        address _lowerHint
    ) external payable;

    function withdrawColl(
        address _asset,
        uint256 _amount,
        address _upperHint,
        address _lowerHint
    ) external;

    function withdrawDCHF(
        address _asset,
        uint256 _maxFee,
        uint256 _amount,
        address _upperHint,
        address _lowerHint
    ) external;

    function repayDCHF(
        address _asset,
        uint256 _amount,
        address _upperHint,
        address _lowerHint
    ) external;

    function closeTrove(address _asset) external;

    function adjustTrove(
        address _asset,
        uint256 _assetSent,
        uint256 _maxFee,
        uint256 _collWithdrawal,
        uint256 _debtChange,
        bool isDebtIncrease,
        address _upperHint,
        address _lowerHint
    ) external payable;

    function claimCollateral(address _asset) external;

    function getCompositeDebt(address _asset, uint256 _debt)
        external
        view
        returns (uint256);

    function isContractBorrowerOps() external pure returns (bool);
}
