        external
        view
        returns (uint256);

    function MAX_BORROWING_FEE(address _collateral)
        external
        view
        returns (uint256);

    function redemptionBlock(address _collateral)
        external
        view
        returns (uint256);

    function activePool() external view returns (IActivePool);

    function defaultPool() external view returns (IDefaultPool);

    function priceFeed() external view returns (IPriceFeed);

