        address _defaultPool,
        address _priceFeed,
        address _adminContract
    ) external;

    function setPriceFeed(address _priceFeed) external;

    function setMCR(address _asset, uint256 newMCR) external;

    function setCCR(address _asset, uint256 newCCR) external;

