    function setMaxBorrowingFee(address _asset, uint256 maxBorrowingFee)
        external;

    function setRedemptionFeeFloor(address _asset, uint256 redemptionFeeFloor)
        external;

    function removeRedemptionBlock(address _asset) external;
}
