pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "./defifranc/interfaces/IBorrowerOperations.sol";

// Uncomment this line to use console.log
// import "hardhat/console.sol";

error HarvestaStaking__NotEnoughEther();

contract HarvestaStaking {
    using SafeMath for uint256;

    /* ====== DEFIFRANC ADDRESSES ====== */

    address private immutable borrowerOperations;
    address private immutable hintHelpers;

    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    // Fallback function is called when msg.data is not empty
    fallback() external payable {}

    constructor(address _borrowerOperations, address _hintHelpers) {
        borrowerOperations = _borrowerOperations;

        hintHelpers = _hintHelpers;
    }

    function openTroveWithHint(
        address _asset,
        uint256 _collEther,
        uint256 _amountDebt
    ) external {}

    function openTrove(
        address _asset,
        uint256 _collEther,
        uint256 _amountDebt
    ) external {
        uint256 _maxFee = 5e16;

        _openTrove(
            _asset,
            _maxFee,
            _amountDebt,
            _collEther,
            address(this),
            address(this)
        );
    }

    function _openTrove(
        address _asset,
        uint256 _maxFee,
        uint256 _amountDebt,
        uint256 _collEther,
        address _upperHint,
        address _lowerHint
    ) internal {
        if (_collEther > getBalance()) {
            revert HarvestaStaking__NotEnoughEther();
        }
        IBorrowerOperations(borrowerOperations).openTrove{value: _collEther}(
            _asset,
            _collEther,
            _maxFee,
            _amountDebt,
            _upperHint,
            _lowerHint
        );
    }

    function getBalance() internal view returns (uint256) {
        return address(this).balance;
    }
}
